DROP TABLE IF EXISTS `news`;
CREATE TABLE `news` (
  `id` int(6) NOT NULL AUTO_INCREMENT,
  `type` int(3) NOT NULL DEFAULT '1',
  `active` int(1) NOT NULL DEFAULT '0',
  `updated` int(10) NOT NULL DEFAULT '0',
  `orders` int(3) NOT NULL DEFAULT '0',
  `title` varchar(255) COLLATE utf8_unicode_ci NOT NULL,
  `keywords` varchar(45) COLLATE utf8_unicode_ci NOT NULL DEFAULT '',
  `content` text COLLATE utf8_unicode_ci NOT NULL,
  `created` int(10) NOT NULL DEFAULT '0',
  `recommend` int(1) NOT NULL DEFAULT '0' COMMENT '首页推荐 ',
  PRIMARY KEY (`id`),
  KEY `type` (`type`,`orders`) USING BTREE,
  KEY `active` (`active`) USING BTREE,
  KEY `orders` (`orders`) USING BTREE
) ENGINE=InnoDB AUTO_INCREMENT=613 DEFAULT CHARSET=utf8 COLLATE=utf8_unicode_ci COMMENT='文档表';

